puzzleinput = '';
inputlength = length(puzzleinput);

% --- Part One ---
packetmarkersize = 4;
for i = 1:(inputlength - packetmarkersize + 1)
    win = substr(puzzleinput,i,packetmarkersize);
    if length(unique(win)) == packetmarkersize
        fprintf("First marker after character %d\n", i + packetmarkersize - 1)
        break
    end
end

% --- Part Two ---
messagemarkersize = 14;
for i = 1:(inputlength - messagemarkersize + 1)
    win = substr(puzzleinput,i,messagemarkersize);
    if length(unique(win)) == messagemarkersize
        fprintf("First marker after character %d\n", i + messagemarkersize - 1)
        break
    end
end


function [substring] = substr(string, beginning, size)
    substring = string(beginning:(beginning+size-1));
end